SELECT TOP 10 * FROM API_LOG WHERE USUARIO='JJIMENEZ' ORDER BY ITEM DESC

SELECT  * FROM USPROH WHERE IDPROCEDIMIENTO ='MENUQ_ADMINISTRATIVO'

SELECT CNSFNOT,CLASE,ITEM,IDSERVICIO,DESCRIPCION,CANTIDAD,VR_UNITARIO,VR_TOTAL,OBSERVACION FROM FNOTD

SELECT FNOT.CNSFNOT,FNOT.CLASE,DBO.FNK_FECHA_GRINGA(FNOT.F_NOTA)F_NOTA,TER.NIT,TER.RAZONSOCIAL,FNOT.N_FACTURA,FNOT.VR_TOTAL,FNOT.PROCEDENCIA,FNOT.IDCONCEPTO,CPNT.DESCRIPCION,FNOT.OBSERVACION,FNOT.CONTABILIZADA,FNOT.NROCOMPROBANTE,FNOT.FACTE,DATOS = (             SELECT  CNSFNOT,CLASE,ITEM,IDSERVICIO,DESCRIPCION,CANTIDAD,VR_UNITARIO,VR_TOTAL,OBSERVACION FROM FNOTD             WHERE FNOTD.CNSFNOT = FNOT.CNSFNOT             FOR JSON PATH )  FROM [dbo].[FNOT] INNER JOIN TER ON FNOT.IDTERCERO=TER.IDTERCERO  LEFT JOIN CPNT ON FNOT.IDCONCEPTO=CPNT.CODIGO WHERE (TER.RAZONSOCIAL LIKE '%%%%' OR CNSFNOT LIKE '%%%%') ORDER BY FNOT.F_NOTA DESC OFFSET (1-1)*10 ROWS  FETCH NEXT 10 ROWS ONLY 

SELECT FNOT.CNSFNOT,FNOT.CLASE,DBO.FNK_FECHA_GRINGA(FNOT.F_NOTA)F_NOTA,TER.NIT,TER.RAZONSOCIAL,FNOT.N_FACTURA,FNOT.VR_TOTAL,FNOT.PROCEDENCIA,FNOT.IDCONCEPTO,CPNT.DESCRIPCION,FNOT.OBSERVACION,FNOT.CONTABILIZADA,FNOT.NROCOMPROBANTE,FNOT.FACTE,DATOS = (             SELECT  CNSFNOT,CLASE,ITEM,IDSERVICIO,DESCRIPCION,CANTIDAD,VR_UNITARIO,VR_TOTAL,FNOTD.OBSERVACION AS OBS FROM FNOTD             WHERE FNOTD.CNSFNOT = FNOT.CNSFNOT             FOR JSON PATH )  FROM [dbo].[FNOT] INNER JOIN TER ON FNOT.IDTERCERO=TER.IDTERCERO  LEFT JOIN CPNT ON FNOT.IDCONCEPTO=CPNT.CODIGO WHERE (TER.RAZONSOCIAL LIKE '%%%%' OR CNSFNOT LIKE '%%%%') ORDER BY FNOT.F_NOTA DESC OFFSET (1-1)*10 ROWS  FETCH NEXT 10 ROWS ONLY 