use PUNIPSSAM
SELECT TOP 10 * FROM API_LOG WHERE USUARIO='JJIMENEZ' ORDER BY ITEM DESC

SELECT  ROW_NUMBER() OVER(ORDER  BY IEXI.IDBODEGA,IEXI.IDARTICULO,IEXI.NOLOTE  ASC) ID,  IEXI.IDARTICULO,IART.DESCRIPCION,             IEXI.EXISLOTE,IEXI.NOLOTEPEDIDO,IEXI.NOLOTE,DBO.FNK_FECHA_GRINGA(IEXI.FECHAVENCE)FECHAVENCE,IART.IDPRINACTIVO,             IPAC.DESCRIPCION AS DESC_PRINCIPIO,IART.IDFORFARM,IFFA.DESCRIPCION AS DESC_FORMA,IART.IDCONCENTRA,ICCN.DESCRIPCION AS DESC_CONCENTRA,             IART.IDUNIDAD,IUNI.DESCRIPCION AS DESC_UNIDAD,IART.CLASERIESGO,COALESCE(TR.DESCRIPCION,'') AS DESC_RIESGP,CASE WHEN COALESCE(IEXI.RINVIMA,'')='' THEN IART.REGINVIMA ELSE IEXI.RINVIMA END RINVIMA,             IART.CODCUM,DBO.FNK_FECHA_GRINGA(IART.F_VIGENCIAINV)F_VIGENCIAINV,IEXI.IDBODEGA,TP.DESCRIPCION AS PRESENTA,IART.IDITAR,             ITAR.DESCRIPCION AS DESC_TIPOART         FROM [dbo].[IEXI] INNER JOIN  IART ON IEXI.IDARTICULO=IART.IDARTICULO  INNER JOIN ITAR ON IART.IDITAR=ITAR.IDITAR  LEFT JOIN IPAC ON IART.IDPRINACTIVO=IPAC.IDPRINACTIVO                 LEFT JOIN IFFA ON IART.IDFORFARM=IFFA.IDFORFARM                                 LEFT JOIN ICCN ON IART.IDCONCENTRA=ICCN.IDCONCENTRA                             LEFT JOIN IUNI ON IART.IDUNIDAD=IUNI.IDUNIDAD                LEFT JOIN TGEN TR ON IART.CLASERIESGO=TR.CODIGO AND TR.TABLA='IART' AND TR.CAMPO='CLASERIESGO'               LEFT JOIN TGEN TP ON IART.PRECOMERCIAL=TP.CODIGO AND TP.TABLA='IART' AND TP.CAMPO='PRECOMERCIAL'  WHERE ( IEXI.IDARTICULO LIKE '%%%%' OR  IART.DESCRIPCION   LIKE '%%%%') AND  AND IEXI.NOLOTEPEDIDO LIKE'%b%'   ORDER BY  IEXI.IDBODEGA,IEXI.IDARTICULO,IEXI.NOLOTE  ASC  OFFSET (1-1)*20 ROWS  FETCH NEXT 20 ROWS ONLY 

SELECT ROW_NUMBER() OVER(ORDER  BY CNS  DESC)ID,ID1 IDARTICULO,NOMBRE DESCARTICULO,VALOR1 EXISTENCIA,VALOR2 SALIDAS  ,VALOR3 PROMEDIO,VALOR4 ENTRADAS,VALOR5 PROM_ENT,VALOR6 APEDIR  FROM [dbo].[RPDX] WHERE (RPDX.NOMBRE LIKE '%%%%') AND  CNS='0200551817'  ORDER BY RPDX.ID1 OFFSET (1-1)*10 ROWS  FETCH NEXT 10 ROWS ONLY 

