SELECT TOP 10 * FROM API_LOG WHERE USUARIO='JJIMENEZ' ORDER BY ITEM DESC

SELECT CEN.CCOSTO, CEN.DESCRIPCION, AFU.IDAREA, AFU.DESCRIPCION AS DESC_AREA  FROM [dbo].[CEN] INNER JOIN AFU ON AFU.IDAREA = CEN.IDAREA WHERE (CEN.CCOSTO LIKE '%%%%' OR  CEN.DESCRIPCION LIKE '%%%%') AND AFU.IDAREA = 5105 AND CEN.ESTADO = 'Activo'  ORDER BY [dbo].[CEN].[COMPANIA] OFFSET (1-1)*10 ROWS  FETCH NEXT 10 ROWS ONLY 