SELECT  FTR.N_FACTURA,FTR.LINKFACTURA,FTR.IDTERCERO,TER.NIT,TER.RAZONSOCIAL,DBO.FNK_FECHA_DDMMAA(FTR.F_FACTURA)F_FACTURA,      DBO.FNK_FECHA_DDMMAA(FTR.F_VENCE)F_VENCE,      FTR.PROCEDENCIA,FTR.NOREFERENCIA,FTR.IDPLAN,FTR.VALORSERVICIOS,CASE COALESCE(COPAPROPIO,0) WHEN 1 THEN CP_VLR_COPAGOS ELSE FTR.VALORCOPAGO END VALORCOPAGO,VALORMODERADORA,       FTR.VR_TOTAL, FTR.ESTADO,       COALESCE(FTR.TIPOANULACION,'') TIPOANULACION,       COALESCE(FACTE,0)FACTE,       CASE COALESCE(FACTE,0) WHEN 0 THEN 'Sin Notificar' WHEN 1 THEN 'En Cola' WHEN 2 THEN 'Notificada' WHEN 3  THEN 'Con Errores' WHEN 4 THEN  'Procesando' END ESTDIAN,       CASE COALESCE(FTR.CONTABILIZADA,0) WHEN 1 THEN 'Si' WHEN 2 THEN 'Error' ELSE 'No' END CONTABILIZADA,       FTR.CNSFCT AS CNSFTR,       FTR.VR_ABONOS AS ABONOS ,       CASE COALESCE(FTR.CAPITADA,0) WHEN 0 THEN 'No' ELSE 'Si' END CAPITADA,       CP_CONVENIO AS CONVENIO,       COPAGOP = COALESCE(COPAPROPIO,0),       VLRCOPAPRO = COALESCE(CP_VLR_COPAGOS,0),       FTR.DESCUENTO,       F_INICIAL = FECHACAP_INI,       F_FINAL = FECHACAP_FIN,       FTR.IDSEDE,       SED.DESCRIPCION AS DESSEDE,       CP_MES AS MES,       MODALIDAD = CP_MODALIDAD,       FTR.OBSERVACION,       TIPOFINAN = TIPOFIN,       PLN.DESCPLAN,       USUARIOFACTURA,       CP_VLR_SERVICIOS,       CP_VLR_COPAGOS,       AFI.DOCIDAFILIADO,       AFI.TIPO_DOC,       AFI.NOMBREAFI,       AFI.EDAD,       FTR.TIPOFAC,       FTR.IDDEP,       FDEP.NOMBRE AS UBICACION    FROM [dbo].[FTR] INNER JOIN TER ON FTR.IDTERCERO=TER.IDTERCERO LEFT JOIN AFI ON FTR.IDAFILIADO=AFI.IDAFILIADO LEFT JOIN PLN ON FTR.IDPLAN=PLN.IDPLAN LEFT JOIN CIT ON CIT.CONSECUTIVO=FTR.NOREFERENCIA LEFT JOIN AUT ON AUT.NOAUT=FTR.NOREFERENCIA LEFT JOIN SED ON FTR.IDSEDE=SED.IDSEDE LEFT JOIN HADM ON HADM.NOADMISION=FTR.NOREFERENCIA LEFT JOIN PPT ON FTR.IDTERCERO=PPT.IDTERCERO AND FTR.IDPLAN=PPT.IDPLAN LEFT JOIN FDEP ON FTR.IDDEP=FDEP.IDDEP WHERE ( FTR.N_FACTURA LIKE '%%%%') AND  COALESCE(FTR.TIPOANULACION,'')<>'NC' AND FTR.ESTADO='P' AND NOT EXISTS(SELECT * FROM FCXCD WHERE FTR.N_FACTURA=FCXCD.N_FACTURA) AND FTR.PROCEDENCIA='undefined'  ORDER BY FTR.N_fACTURA OFFSET (1-1)*10 ROWS  FETCH NEXT 10 ROWS ONLY 

