SELECT IDAUT, NO_ITEM,AUTD.IDSERVICIO,AUTD.CANTIDAD,SER.DESCSERVICIO,AUTD.VALOR,AUTD.VALORCOPAGO,NOAUTORIZEXT  FROM [dbo].[AUTD] INNER JOIN  SER ON AUTD.IDSERVICIO=SER.IDSERVICIO  WHERE (AUT.IDSERVICIO LIKE '%%%%') AND  AUTD.IDAUT='0202405798' AND COALESCE(AUD.FACTURADA,0)=0 AND COALESCE(AUTD.NOCOBRABLE,0)=0   ORDER BY AUTD.IDAUT,AUTD.NO_ITEM  ASC OFFSET (1-1)*10 ROWS  FETCH NEXT 10 ROWS ONLY 

